function accordion(a){var b={slideUpDuration:500,slideDownDuration:500,closeOther:true};a=$.extend({},b,a);$(".va-accordion>:header").click(function(){$(".va-accordion>div").each(function(){$(this).is(":visible")&&a.closeOther&&$(this).slideUp(a.slideUpDuration)});if(!$(this).next().is(":visible"))$(this).next().slideDown(a.slideDownDuration);else!a.closeOther&&$(this).next().slideUp(a.slideDownDuration)})}